import React, { useState, useEffect } from 'react';

const API_URL = "https://opentdb.com/api.php?amount=30&category=17&difficulty=easy"

const Trivia = () => {
    const [trivia, setTrivia ] = useState ([]);

    useEffect(() => {
        fetch(API_URL)
        .then(resp => resp.json())
        .then(json => {
            setTrivia(json.results)
        })
    }, []);

    return (
        <div>
            {trivia.map(triv => (
                <p>{triv.question}</p>
            ))}
        </div>
    )

}

export default Trivia;
